const Products = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка загрузки продуктов');
        }
        return response.json();
      })
      .then(data => setProducts(data))
      .catch(err => console.error('Ошибка:', err));
