import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Logout = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  // Проверка состояния авторизации
  useEffect(() => {
    const checkAuth = async () => {
      try {
        const accessToken = localStorage.getItem('accessToken');
        if (accessToken) {
          setIsLoggedIn(true);  // Пользователь авторизован
        }
      } catch (error) {
        console.error('Ошибка при проверке авторизации:', error);
      }
    };

    checkAuth();
  }, []);

  const handleLogout = async () => {
    try {
      const refreshToken = localStorage.getItem('refreshToken');

      // Отправка запроса на выход
      await axios.post('/api/auth/logout', { refreshToken }, { withCredentials: true });

      // Очистка токенов
      localStorage.removeItem('accessToken');
      localStorage.removeItem('refreshToken');

      setIsLoggedIn(false);  // Обновление состояния
      navigate('/');
    } catch (error) {
      console.error('Ошибка при выходе:', error);
    }
  };

  return (
    <div className="logout-container">
      {isLoggedIn ? (
        <>
          <h2>Вы уверены, что хотите выйти?</h2>
          <button onClick={handleLogout}>Выйти</button>
        </>
      ) : (
        <h2>Вы уже вышли из системы</h2>
      )}
    </div>
  );
};

export default Logout;
